import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar';
import styles from './WeatherDisplay.module.css';

const WeatherDisplay = () => {
  const apiKey = '6800caad12320e5ffdaba91392446b18';
  const [weatherData, setWeatherData] = useState(null);
  const [unit, setUnit] = useState('C');

  const fetchWeatherData = async (location) => {
    const response = await fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${apiKey}&units=metric`
    );
    const data = await response.json();
    setWeatherData(data);
  };

  useEffect(() => {
    fetchWeatherData('New York');
  }, []);

  const toggleUnit = () => {
    setUnit(unit === 'C' ? 'F' : 'C');
  };

  const convertTemp = (temp) => {
    return unit === 'C' ? temp : (temp * 9) / 5 + 32;
  };

  const displayWeatherData = () => {
    if (weatherData && weatherData.sys) {
      return (
        <div>
          <h2>
            {weatherData.name}, {weatherData.sys.country}
          </h2>
          <h3>{weatherData.weather[0].main}</h3>
          <p>{weatherData.weather[0].description}</p>
          <p>Temperature: {convertTemp(weatherData.main.temp)}°{unit}</p>
          <p>Feels like: {convertTemp(weatherData.main.feels_like)}°{unit}</p>
          <button onClick={toggleUnit}>Switch to °{unit === 'C' ? 'F' : 'C'}</button>
        </div>
      );
    } else {
      return <p>Loading weather data...</p>;
    }
  };

  return (
    <div className={styles.weatherContainer}>
      <SearchBar fetchWeatherData={fetchWeatherData} />
      {displayWeatherData()}
    </div>
  );
};

export default WeatherDisplay;
