import React, { useState, useEffect } from "react";
import axios from "axios";
import ScoreTracker from "./ScoreTracker";

function App() {
  const [guess, setGuess] = useState("");
  const [history, setHistory] = useState([]);
  const [secretWord, setSecretWord] = useState("");
  const [score, setScore] = useState(0);

  const fetchWord = async () => {
    try {
      const result = await axios.get('https://owlbot.info/api/v4/dictionary/random', {
        headers: {
          'Authorization': 'Token YOUR_API_KEY'
        }
      });
      const word = result.data.word.toLowerCase();
      if (word.length === 5) {
        setSecretWord(word);
      } else {
        fetchWord();
      }
    } catch (error) {
      console.error("Error fetching word: ", error);
    }
  };

  useEffect(() => {
    fetchWord();
  }, []);

  const handleChange = (e) => {
    setGuess(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (guess.length === 5) {
      let guessHistory = {
        guess: guess,
        matches: guess.split("").map((g, i) => {
          if (g === secretWord[i]) {
            return 'correct-position';
          } else if (secretWord.includes(g)) {
            return 'wrong-position';
          } else {
            return 'incorrect';
          }
        })
      };
      setHistory([...history, guessHistory]);
      if (guess === secretWord) {
        setScore(score + 1);
        setGuess("");
        fetchWord();
      } else {
        setGuess("");
      }
    }
  };

  return (
    <div className="App">
      <h1>Wordle Clone</h1>
      <ScoreTracker score={score} />
      <form onSubmit={handleSubmit}>
        <input type="text" value={guess} onChange={handleChange} maxLength="5" />
        <button type="submit">Guess</button>
      </form>
      <div>
        {history.map((h, index) => (
          <p key={index}>
            {h.guess.split("").map((char, i) => (
              <span 
                key={i} 
                style={{ 
                  color: h.matches[i] === 'correct-position' 
                    ? 'green' 
                    : h.matches[i] === 'wrong-position'
                    ? 'yellow'
                    : 'red'
                }}>
                {char}
              </span>
            ))}
          </p>
        ))}
      </div>
    </div>
  );
}

export default App;
