import React, { useState } from 'react';
import styles from './SearchBar.module.css';


function SearchBar() {
  // Component logic goes here
  const [searchTerm, setSearchTerm] = useState('');
  const handleInputChange = (e) => {
    setSearchTerm(e.target.value);
  };
  const handleSearch = (e) => {
    e.preventDefault();
    // Call the API or pass searchTerm to the parent component
    console.log('Searching for:', searchTerm);
  };

  return (
    <form onSubmit={handleSearch} className={`${styles.searchbarContainer}`}>
      <input
        type="text"
        value={searchTerm}
        onChange={handleInputChange}
        placeholder="Search location"
        className={`border border-gray-300 p-2 rounded-md focus:outline-none focus:border-blue-300 ${styles.searchInput}`}
      />
      <button
        type="submit"
        className={`ml-2 bg-blue-500 text-white p-2 rounded-md ${styles.searchButton}`}
      >
        Search
      </button>
    </form>
  );



}

export default SearchBar;
